<?phpclass M_communes extends CI_Model {    public $_table_name = 'communes';    public function __construst() {        parent::__construst();    }    private function generate_where($args) {        if (isset($args['q'])) {            $this->db->group_start();            $this->db->like($this->_table_name . ".name", $args['q']);            $this->db->group_end();        }        if (isset($args['not_in_id'])) {            $this->db->where_not_in($this->_table_name . ".id", $args['not_in_id']);        }        if (isset($args['in_id'])) {            $this->db->where_in($this->_table_name . ".id", $args['in_id']);        }        if (isset($args['district_id'])) {            $this->db->where($this->_table_name . ".district_id", $args['district_id']);        }		if (isset($args['in_district_id'])) {            $this->db->where_in($this->_table_name . ".district_id", $args['in_district_id']);        }		if (isset($args['in_province_id'])) {            $this->db->where_in("provinces.pId", $args['in_province_id']);        }    }    private function generate_order_by($args) {        $allow_sort = array("DESC", "ASC");        if (isset($args['order_by']) && is_array($args['order_by']) && !empty($args['order_by'])) {            foreach ($args['order_by'] as $key => $value) {                $sort = in_array($value, $allow_sort) ? $value : "DESC";                $this->db->order_by($key, $sort);            }        }    }    public function gets($args, $perpage = 5, $offset = -1) {        $this->db->select($this->_table_name . '.*,' . 'provinces.pId, provinces.pNameVi as province_name, districts.dId, districts.dName as district_name');        $this->db->from($this->_table_name);        $this->db->join('districts', 'districts.dId = ' . $this->_table_name . '.district_id', 'left');		$this->db->join('provinces', 'provinces.pId = ' . 'districts.pId', 'left');        $this->generate_where($args);        $this->generate_order_by($args);        if ($offset >= 0) {            $this->db->limit($perpage, $offset);        }        $query = $this->db->get();        return $query->result_array();    }    public function counts($args) {        $this->db->select();        $this->db->from($this->_table_name);        $this->db->join('districts', 'districts.dId = ' . $this->_table_name . '.district_id', 'left');        $this->db->join('provinces', 'provinces.pId = ' . 'districts.pId', 'left');        $this->generate_where($args);        $query = $this->db->get();        return $query->num_rows();    }    public function get($id) {        $this->db->select($this->_table_name . '.*,' . 'provinces.pId, provinces.pNameVi as province_name, districts.dId, districts.dName as district_name');        $this->db->from($this->_table_name);        $this->db->join('districts', 'districts.dId = ' . $this->_table_name . '.district_id', 'left');        $this->db->join('provinces', 'provinces.pId = ' . 'districts.pId', 'left');        $this->db->where($this->_table_name . '.id', $id);        $query = $this->db->get();        return $query->row_array();    }    public function add($data = array()) {        if (empty($data)) {            return 0;        }        $query = $this->db->insert($this->_table_name, $data);        $insert_id = $this->db->insert_id();        return (isset($query)) ? $insert_id : 0;    }    public function update($id, $data) {        if (empty($data)) {            return FALSE;        }        $this->db->where('id', $id);        $query = $this->db->update($this->_table_name, $data);        return (isset($query)) ? true : false;    }    public function delete($id) {        $this->db->where('id', $id);        $query = $this->db->delete($this->_table_name);        return (isset($query)) ? true : false;    }}/* End of file m_communes.php *//* Location: ./application/modules/provinces/models/m_communes.php */