<?phpclass M_xaphuongthitran extends CI_Model {    public $_table_name = 'devvn_xaphuongthitran';    public function __construst() {        parent::__construst();    }    private function generate_where($args) {        if (isset($args['maqh'])) {            $this->db->where($this->_table_name . ".maqh", $args['maqh']);        }				if (isset($args['xaid'])) {            $this->db->where($this->_table_name . ".xaid", $args['xaid']);        }    }    private function generate_order_by($args) {        $allow_sort = array("DESC", "ASC");        if (isset($args['order_by']) && is_array($args['order_by']) && !empty($args['order_by'])) {            foreach ($args['order_by'] as $key => $value) {                $sort = in_array($value, $allow_sort) ? $value : "DESC";                $this->db->order_by($this->_table_name . '.' . $key, $sort);            }        }    }    public function gets($args, $perpage = 5, $offset = -1) {        $this->db->select();        $this->db->from($this->_table_name);        $this->generate_where($args);        $this->generate_order_by($args);        if ($offset >= 0) {            $this->db->limit($perpage, $offset);        }        $query = $this->db->get();        return $query->result_array();    }    public function get($id) {        $this->db->select();        $this->db->from($this->_table_name);        $this->db->where($this->_table_name . '.xaid', $id);        $query = $this->db->get();        return $query->row_array();    }}/* End of file m_xaphuongthitran.php *//* Location: ./application/modules/xaphuongthitran/models/m_xaphuongthitran.php */