<div class="row">    <div class="col-xs-12 col-sm-12 col-md-12 col-lg-12">        <div class="box box-primary">            <div class="box-header with-border">                <h3 class="box-title"><em class="fa fa-table">&nbsp;</em>Thông tin tỉnh/thành phố</h3>            </div>            <div class="box-body">                <form id="f-content" action="<?php echo get_admin_url($module_slug . '/content' . (isset($row['pId']) ? '/' . $row['pId'] : '')); ?>" method="post" autocomplete="off">                    <?php                    if (isset($row['pId'])) {                    	echo '<input type="hidden" value="' . $row['pId'] . '" id="id" name="id" />';                    }                    ?>                    <div class="row">                        <div class="col-md-3">                            <div class="form-group required<?php echo form_error('pNameVi') != '' ? ' has-error' : ''; ?>">                                <label class="control-label">Tên tỉnh/thành phố</label>                                <input type="text" class="form-control" name="pNameVi" value="<?php echo isset($row['pNameVi']) ? html_escape($row['pNameVi']) : ''; ?>">                                <?php echo form_error('pNameVi'); ?>                            </div>                        </div>                        <div class="col-md-2">							<div class="form-group required<?php echo form_error('cost') != '' ? ' has-error' : ''; ?>">                                <label class="control-label">Phí</label>                                <input type="text" class="form-control text-right mask-price" name="cost" value="<?php echo isset($row['cost']) ? $row['cost'] : ''; ?>">                                <?php echo form_error('cost'); ?>                            </div>                        </div>                        <div class="col-md-4">                            <div class="form-group">                                <label class="control-label">&nbsp;</label>                                <br>                                <?php if (isset($row['pId'])) : ?>                                    <button type="submit" class="btn btn-primary">Lưu thay đổi</button>                                <?php else : ?>                                    <button type="submit" class="btn btn-success">Thêm</button>                                <?php endif; ?>                            </div>                        </div>                    </div>                </form>            </div>        </div>    </div></div>